from db import Database
from config import Connect
from gecko import Gecko
from web3 import Web3
import time
class Trade:
    def __init__(self):
        
        self.db = Database()
        self.connection = Connect()
        self.gecko = Gecko()
        self.web3 = Web3(Web3.HTTPProvider('https://bsc-dataseed.binance.org/'))
        self.check_all_trades()

    def check_all_trades(self):

        while True:

            time.sleep(3)
            trades = self.db.get_all_trades()
            for x in trades:
                
                contract = x[0]
                amount = x[1]
                method = x[2]
                value = float(x[3])
                id = x[4]
                tx_status = None

                if method =='1' or method == '2':
                    last_RSI = self.gecko.get_last_rsi(id,contract)
                    print('Name: ',id,';Amount to trade:',amount,';Method: ',method,';Last RSI:',last_RSI,';Value looking for:',value)
                    if method == '1':
                        if last_RSI <= value:
                            tx_status = self.make_trade(contract,amount)

                    else:
                        if last_RSI >= value:
                            tx_status = self.make_trade(contract,amount)


                else:
                    last_price = self.gecko.get_last_price(contract)
                    print('Name: ',id,';Amount to trade: ',amount,';Last price: ',last_price,';Value looking for: ',value)
                    if method == '3':
                        if last_price <= value:
                            tx_status = self.make_trade(contract,amount)

                    else:
                        if last_price >= value:
                            tx_status = self.make_trade(contract,amount)
                if tx_status:
                    print('Made trade!!')
                    self.db.delete_trade(contract)
                    self.db.store_past_trades(contract,id, str(time.time()),amount,str(tx_status),method)

                elif tx_status == False:
                    print('Failed trade...')
                    self.db.store_past_trades(contract,id, str(time.time()),amount,str(tx_status),method)

