        contract = self.web3.eth.contract(address=pan_contract, abi=pan_abi)
        token_to_buy = self.web3.toChecksumAddress(contract_addr)           
        spend = self.web3.toChecksumAddress("0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c") 
        nonce = self.web3.eth.get_transaction_count(user_addr)

        pancakeswap2_txn = contract.functions.swapExactETHForTokens(
        0,#MINIMUM AMOUNT
        [spend,token_to_buy],
        user_addr,
        (int(time.time()) + 10000)
        ).buildTransaction({
        'from': user_addr,
        'value': self.web3.toWei(amount,'ether'),#WBNB AMOUNT TO SWAP FROM
        'gas': 250000,
        'gasPrice': self.web3.toWei('5','gwei'),
        'nonce': nonce,
        })
        signed_txn = self.web3.eth.account.sign_transaction(pancakeswap2_txn, private_key=user_key)

        try:
            tx_token = self.web3.eth.send_raw_transaction(signed_txn.rawTransaction)
        except Exception as e:
            print('Transaction error:',e)
            return False
        time.sleep(10)
        
        while True:
            try:

                tx_status = self.web3.eth.getTransactionReceipt(tx_token)
            except Exception as e:
                print(e)
                time.sleep(2)
            else:
                break

        return tx_status['status']            
                
